<script>
  import { onMount } from "svelte";
  let { data } = $props();

  const heading = data.heading || "";
  const body = data.body || "";
  const testimonials = data.testimonials?.length
    ? data.testimonials
    : data.quote
      ? [data.quote]
      : [{ text: "", author: "", authorLabel: "" }];
  const ctas = data.ctas?.length
    ? data.ctas
    : data.buttonSecondary
      ? [
          {
            label: data.buttonSecondary.label,
            link: data.buttonSecondary.path,
            style: "primary",
          },
        ]
      : [];

  import { underlineLastWord } from "$lib/utils/heading";
  import { marked } from "marked";

  let activeIndex = $state(0);

  onMount(() => {
    if (testimonials.length > 1) {
      const interval = setInterval(() => {
        activeIndex = (activeIndex + 1) % testimonials.length;
      }, 5000);
      return () => clearInterval(interval);
    }
  });
</script>

<section class="py-8 md:py-12 bg-transparent relative overflow-hidden">
  <div class="container mx-auto px-4">
    <div class=" mx-auto">
      <!-- Heading Row -->
      <h2
        class="font-header text-4xl md:text-5xl lg:text-6xl text-slate-light text-left mb-4 md:mb-8"
      >
        {@html underlineLastWord(heading)}
      </h2>

      <!-- Content Layout / Purple left, text right -->
      <!-- xx:bg-[linear-gradient(to_right,rgba(74,71,214,1)_0%,rgba(138,120,255,1)_55%,rgba(138,120,255,0.12)_55%,rgba(138,120,255,0.12)_100%)] -->
      <div
        class="grid grid-cols-1 lg:grid-cols-2 gap-0 rounded-[2rem] overflow-hidden bg-(--color-purple)"
      >
        <!-- Left Quote Card -->
        <div class="flex justify-start items-center p-6 md:p-8">
          <div
            class="w-full max-w-md lg:max-w-none bg-(--color-purple) p-6 md:p-8 lg:mx-0 mx-auto rounded-sm relative flex flex-col justify-center md:aspect-square"
          >
            <!-- Header Labels -->
            <div
              class="flex justify-between items-center mb-6 border-b-2 border-white/30 pb-2 relative"
            >
              <div
                class="flex justify-between w-full text-xs font-bold text-white tracking-widest uppercase"
              >
                <span>People</span>
                <span>Power</span>
                <span class="u-underlined">Impact</span>
              </div>
            </div>

            <!-- Quote -->
            <div
              class="bg-white text-black p-8 shadow-lg relative rounded-sm transform -rotate-1 grid grid-cols-1"
            >
              {#each testimonials as t, i}
                <div
                  class="col-start-1 row-start-1 flex flex-col transition-opacity duration-1000 {i ===
                  activeIndex
                    ? 'opacity-100 z-10'
                    : 'opacity-0 z-0 pointer-events-none'}"
                >
                  <blockquote
                    class="font-serif font-bold text-3xl md:text-4xl leading-wide mb-6 uppercase"
                  >
                    "{t.text}"
                  </blockquote>

                  <!-- Orange Decoration -->
                  <div class="scribble-underline mb-4"></div>

                  <div class="flex flex-col">
                    <span class="font-bold text-(--color-orange) text-xl"
                      >{t.author}</span
                    >
                    <span
                      class="font-bold text-white text-sm tracking-wide mt-1"
                    >
                      <span class="bg-(--color-purple) px-2 py-1"
                        >{t.authorLabel}</span
                      >
                    </span>
                  </div>
                </div>
              {/each}
            </div>
          </div>
        </div>

        <!-- Right Content -->
        <div
          class="flex flex-col justify-center gap-6 text-center md:text-left items-center md:items-start p-6 md:p-8 bg-(--color-orange) prose prose-invert max-w-none"
        >
          <div
            class="text-lg md:text-xl font-medium opacity-90 leading-relaxed text-white"
          >
            {@html marked.parse(body)}
          </div>

          <!-- Donate Button -->
          <div
            class="self-end flex flex-wrap gap-4 justify-center md:justify-end"
          >
            {#each ctas as cta}
              <a
                href={cta.link}
                class="u-arrow inline-flex items-center justify-center px-5 py-3 rounded-md font-bold uppercase tracking-wide shadow-md transition {cta.style ===
                'secondary'
                  ? 'bg-transparent text-white border-2 border-white hover:bg-white/10'
                  : 'bg-(--color-purple) text-white hover:bg-(--color-purple)/90'}"
              >
                {cta.label}
              </a>
            {/each}
          </div>
        </div>
      </div>
    </div>
  </div>
</section>

